using System;using System.Collections.Generic;using RougeLike.Objects;using OkuBase.Graphics;namespace RougeLike.Renderers{  public class ImageRenderer : IEntityRenderer  {    private string _imageName = null;    private ImageBase _image = null;        public ImageRenderer(string imageName)    {      _imageName = imageName;    }      public void Init()    {      _image = GameUtil.LoadImage(_imageName);    }        public void Begin(EntityObject entity)    {    }        public void Update(float dt, EntityObject entity)    {    }        public void Render(EntityObject entity)    {      OkuBase.OkuManager.Instance.Graphics.DrawImage(0, 0, _image);    }        public void End(EntityObject entity)    {    }        public void Finish()    {      OkuBase.OkuManager.Instance.Graphics.ReleaseImage(_image);    }      }}